def get_frozencore(cas):
    structure_string = structures[cas]
    lines = structure_string.split('\n')
    natom = int(lines[0])
    frozen_orbitals = 0
    for iatom in range(natom):
        element = lines[iatom+2].split()[0]
        frozen_orbitals += frozencore[element]
    return frozen_orbitals

